// ROSALIND Problem 3
// In DNA strings, symbols 'A' and 'T' are complements of each other, as are 'C' and 'G'.
// The reverse complement of a DNA string sss is the string formed by reversing the symbols of sss, then taking the complement of each symbol (e.g., the reverse complement of "GTCA" is "TGAC").
// Given: A DNA string sss of length at most 1000 bp.
// Return: The reverse complement scscsc of sss.
// Sample Dataset: AAAACCCGGT
// Sample Output: ACCGGGTTTT

package main

import (
	"fmt"
)

// write a function to reverse my string (after complementing)
func Reverse(r string) string {
	s := []byte(r)
	for i, j := 0, len(s)-1; i < len(s)/2; i, j = i+1, j-1 {
		s[i], s[j] = s[j], s[i]
	}
	return string(s)
}

func main() {

	// insert my string as bytes
	//var s = []byte("")
	var s = []byte("")
	// complement each nucleotide
	for i, base := range s {
		if base == 'A' {
			s[i] = 'T'
		}
		if base == 'T' {
			s[i] = 'A'
		}
		if base == 'C' {
			s[i] = 'G'
		}
		if base == 'G' {
			s[i] = 'C'
		}
	}

	// convert my complemented string to string form (currently in byte form, need it in string form for func Reverse)
	comp := string(s)
	// reverse my already-complemented string
	revcomp := Reverse(comp)
	// print the reverse complement of my string
	fmt.Printf("%s\n", revcomp)
}
